#include <stdio.h>
#include <stdlib.h>
#include <string.h>

void print_top3(int *top3) {
	int i;
	for (i = 0; i < 3; i++) {
		printf("%d\n", top3[i]);
	}
}

int main() {
	char buf[256];
	int top3[3];
	int n, i, trade;

	// initialize
	memset(top3, 0, sizeof(int) * 3);

	while (fgets(buf, sizeof(buf), stdin)) {
		n = atoi(buf);

		if (n <= buf[2]) { break; }

		top3[2] = n;
		for (i = 1; 0 <= i; i--) {
			if (top3[i] < top3[i + 1]) {
				trade = top3[i];
				top3[i] = top3[i + 1];
				top3[i + 1] = trade;
			}
		}
	}

	print_top3(top3);

	return 0;
}

