using namespace std;

//x[a]で定義される配列を降順に並び替える
void sort_array(int x[],int a){
	int i,j,tmp;
	for(i=0;i<a-1;i++){
		for(j = i+1;j < a ; j++){
			if(x[i] < x[j]){
				tmp = x[i];
				x[i] = x[j];
                x[j] = tmp;
			}
		}
	}
}	

int main(){
	int x[10];
	int i;
	for(i = 0; i< 10; i++)
	{
		cin >> x[i];
	}
	sort_array(x , 10);
	cout << x[0] << endl;
	cout << x[1] << endl;
	cout << x[2] << endl;
	return 0;
}

