#include <stdio.h>
void SimpleSelectionSort(int* array,int n){
    int temp=0;
    int i = 0;
    int j = 0;

    for(i = 0;i<n-1;i++){
        for(j=i+1;j<n;j++){
            if(array[i] < array[j]){
                temp = array[i];
                array[i] = array[j];
                array[j] = temp;
            }
        }
    }

    return;

}

int main(void){
    int array[10];
    int i = 0;
    for(i=0;i<10;i++){
        scanf("%d" ,&array[i]);
    }

    SimpleSelectionSort(array , 10);

    for(i = 0;i<3;i++){
        printf("%d\n",array[i]);
    }

    return 0;
}

