void sort(int data[]) {
    for(int i = 0; i < 9; i++) {
        for(int j = i; j < 10; j++) {
            if(data[i] < data[j]) {
                int tmp = data[i];
                data[i] = data[j];
                data[j] = tmp;
            }
        }
    }
}

