    int tmp;
    for(int ii=0;ii<3;ii++){
        if(a[ii] < in){            
            tmp = a[ii];
            a[ii] = in;

            for(int jj=ii+1;jj<3;jj++){
                int tmp2;
                tmp2 = a[jj];
                a[jj] = tmp;
                tmp = tmp2;
            }
