void sort(int A[])
{
	int i, j, min;
	for (i = 0; i < 10; i++)
		min = i;
	for (j = i; j < 10; j++)
	{
		if(A[j] < A[min])
		{
			swap(A[j], A[min]);
			min = j;
		}
	}
	for (i = 0; i < 3; i++)
	{
		cout<<A[i]<<endl;
	}
}
void swap(int &a, int &b)
{
	int t = a;
	a = b;
	b = t;
}

