#include <iostream>
#include <algorithm>
#include <vector>

static const int DataMax = 200;	// Max value of elements

typedef struct _data_list
{
    int count;	// Number of elements
    std::vector<int> vec[DataMax];
} data_list_t;

static void InputData(data_list_t& data)
{
    int v1, v2;

    if (0x00 == &data) {
        std::cerr << "data is null" << std::endl;
        exit (1);
    }

    while (std::cin >> v1 >> v2) {
        data.vec[data.count].push_back(v1);
        data.vec[data.count].push_back(v2);
        data.count++;
    }
}

static int getDigitSumOfValues(data_list_t& data, int index)
{
    int digit = 0;
    int sum = 0;

    if (0x00 == &data) {
        std::cerr << "data is null" << std::endl;
        exit (1);
    }

    for (int i = 0; i < data.vec[index].size(); i++) {
            sum += data.vec[index].at(i);
    }

    while (sum >= 1) {
        digit++;
        sum /= 10;
    }

    return digit;
}

int main()
{
    data_list_t data = {0};

    InputData(data);
    for (int i = 0; i < data.count; i++) {
        std::cout << getDigitSumOfValues(data, i) << std::endl;
    }

    return 0;
}

