    int a = 0;
    int b = 0;

    std::cin >> a;
    std::cin >> b;    

    int c = a + b;

    int count = 1;

    while (c >= M) {
      c /= M;
      count++;

      if (c < M) {
	break;
      }
    }

    std::cout << count << std::endl;
  }

  return 0;
}

