#include <algorithm>
#include <iostream>
#include <list>
using namespace std;

int check_digit(int num) {
  int digit = 0;
  while (true) {
    num /= 10;
    digit++;
    if (num <= 0) {
      break;
    }
  }
  return digit;
}

int digit_count(int a, int b) {
  int digit;
  digit = check_digit(a + b);
  return digit;
}

int main(int argc, char const *argv[]) {
  int a, b;
  list<int> ls;
  while (!(std::cin >> a >> b).eof()) {
    ls.push_back(digit_count(a, b));
  }

  list<int>::iterator it = ls.begin();
  while (it != ls.end()) {
    cout << *it << endl;
    it++;
  }
}

