bool isRightTriangle(vector<int> &vecSide)
{
    int a,b,c;

    if (vecSide[0] > vecSide[1]) {
        swap(vecSide[0], vecSide[1]);
    }
    if (vecSide[1] > vecSide[2]) {
        swap(vecSide[1], vecSide[2]);
    }

    a = vecSide[0];
    b = vecSide[1];
    c = vecSide[2];

    if (c * c == a * a + b * b) {
        return true;
    } else {
        return false;
    }
}

