bool check_triangle(int a, int b, int c)
{
	if (a+b<c) 		return false;
	else if(a+c<b) 	return false;
	else if(b+c<a) 	return false;
	else {
		int cc = max(a,(max(b,c)));
		if (cc*cc == a*a + b*b)
			return true;
		else
			return false;
	}			
}

