bool check_triangle(int a, int b, int c)
{

	vector<int> edges;
	edges.push_back(a);
	edges.push_back(b);
	edges.push_back(c);
	sort(edges.begin(),edges.end());

	if (edges[0]*edges[0] + edges[1]*edges[1]　== edges[2]*edges[2])
		return true;
	else
		return false;		
