int grundy(ll x,ll MAX){

	ll left = MAX/2+1,right = MAX;

	int this_even,this_odd,pre_even,pre_odd;

	while(true){

		if(left == MAX/2+1){ //最初の区間

			if(right%2 == 0){

				this_even = 0; //右端は手詰まりなのでgrundyが0
				this_odd = 1;

			}else{ //right%2 == 1

				this_even = 1;
				this_odd = 0;
			}

		}else{

			bool check[3];
