				check[pre_even] = true;  //+1および*2
			}

			for(int i = 0; i < 3; i++){

				if(!check[i]){

					right_grundy = i;
					break;
				}
			}

			int another_grundy;

			//右端の1つ左のgrundy数を求める
			for(int i = 0; i < 3; i++){

				check[i] = false;
			}

			check[right_grundy] = true; //+1
			check[pre_even] = true; //*2

			for(int i = 0; i < 3; i++){

				if(!check[i]){

					another_grundy = i;
					break;
				}
			}

			if(right%2 == 0){

				this_even = right_grundy;
				this_odd = another_grundy;

			}else{

				this_even = another_grundy;
				this_odd = right_grundy;
			}
		}

		if(x >= left && x <= right){

			if(x%2 == 0){

				return this_even;
			}else{

				return this_odd;
			}
		}

