#include<iostream>
using namespace std;
long long mod=998244353;
long long power(long long a,long long b)
{
	return b?power(a*a%mod,b/2)*(b%2?a:1)%mod:1;
}
long long inv[1111];
long long comb[1111][1111];
long long Pow[1111];
int main()
{
	long long M,N;
	int K;
	cin>>M>>N>>K;
	for(int i=1;i<=K;i++)
	{
		inv[i]=power(i,mod-2);
		Pow[i]=power(i,N);
	}
	comb[0][0]=1;
	for(int i=1;i<=K;i++)
	{
		comb[i][0]=1;
		for(int j=1;j<i;j++)
		{
			comb[i][j]=(comb[i-1][j-1]+comb[i-1][j])%mod;
		}
		comb[i][i]=1;
	}
	long long ans=power(M%mod,N);
	for(int i=1;i<K;i++)
	{
		long long MCi=1;
		for(int j=1;j<=i;j++)
		{
			MCi=(M-j+1)%mod*MCi%mod*inv[j]%mod;
		}
		long long sum=0;
		for(int j=1;j<=i;j++)
		{
			long now=comb[i][j]*Pow[j]%mod;
			if((i-j)%2==1)now=(mod-now)%mod;
			(sum+=now)%=mod;
		}
		(MCi*=sum)%=mod;
		long now=MCi;
		//if((K-i+1)%2==1)now=(mod-now)%mod;
		//cout<<now<<endl;
		(ans-=now)%=mod;
	}
	cout<<(ans+mod)%mod<<endl;
}
