        }
    }
    std::string xs = !rstr.empty() ? "AND CENTS " + trans_two(rstr) + " " : "";
    if (lm.empty()) {
        return "ZERO ONLY";
    }
    else {
        int nowpos = 0;
        for (int i = lm.size() - 1; i >= 0; --i)
        {
            if (lm[i] != ' ')
            {
                nowpos = i;
                break;
            }
        }
        lm = lm.substr(0, nowpos + 1);
        return lm + " " + xs + "ONLY";
    }
}

std::string NumberWordFormatter::format(std::nullptr_t) {
    return "";
}

std::string NumberWordFormatter::trans_two(const std::string& s) {
    std::string ss = s;
    if (ss.size() == 1) ss = "0" + ss;

    if (ss[0] == '0') {
        return NUMBER[ss[1] - '0'];
    }
    else if (ss[0] == '1') {
        return NUMBER_TEEN[std::stoi(ss) - 10];
    }
    else if (ss[1] == '0') {
        return NUMBER_TEN[ss[0] - '1'];
    }
    else {
        return NUMBER_TEN[ss[0] - '1'] + " " + NUMBER[ss[1] - '0'];
    }
}

std::string NumberWordFormatter::trans_three(const std::string& s) {
    if (s[0] == '0') {
        return trans_two(s.substr(1));
    }
    else if (s.substr(1) == "00") {
        return NUMBER[s[0] - '0'] + " HUNDRED";
    }
    else {
        return NUMBER[s[0] - '0'] + " HUNDRED AND " + trans_two(s.substr(1));
    }
}

std::string NumberWordFormatter::parse_more(int i) {
    return NUMBER_MORE[i];
}

#include <vector>
#include <map>
#include <algorithm>
#include <numeric>
#include <cmath>
#include <stdexcept>

class DataStatistics {
public:
    double mean(const std::vector<double>& data) const;

    double median(std::vector<double> data) const;

    std::vector<int> mode(const std::vector<int>& data) const;
};


double DataStatistics::mean(const std::vector<double>& data) const {
    if (data.empty()) {
        throw std::invalid_argument("Data vector is empty.");
    }
    double sum = std::accumulate(data.begin(), data.end(), 0.0);
    return std::round(sum / data.size() * 100) / 100; 
}


double DataStatistics::median(std::vector<double> data) const {
    if (data.empty()) {
        throw std::invalid_argument("Data vector is empty.");
    }
    std::sort(data.begin(), data.end());
    size_t n = data.size();
    if (n % 2 == 0) {
        return std::round(((data[n / 2 - 1] + data[n / 2]) / 2) * 100) / 100; 
    }
    else {
        return data[n / 2];
    }
}


std::vector<int> DataStatistics::mode(const std::vector<int>& data) const {
    if (data.empty()) {
        throw std::invalid_argument("Data vector is empty.");
    }

    std::map<int, int> count_map;
    for (int num : data) {
        ++count_map[num];
    }

    int max_count = 0;
    for (const auto& pair : count_map) {
        if (pair.second > max_count) {
            max_count = pair.second;
        }
    }

    std::vector<int> modes;
    for (const auto& pair : count_map) {
        if (pair.second == max_count) {
            modes.push_back(pair.first);
        }
    }

    return modes;
}

#include <vector>
#include <optional>

class GomokuGame {
public:
    explicit GomokuGame(int board_size);
    bool make_move(int row, int col);
    std::optional<char> check_winner() const;
    std::vector<std::vector<char>> getBoard() const;
    bool _check_five_in_a_row(int row, int col, std::pair<int, int> direction) const;
    int board_size;
    std::vector<std::vector<char>> board;
    char current_player;
};

GomokuGame::GomokuGame(int board_size)
    : board_size(board_size),
    board(board_size, std::vector<char>(board_size, ' ')),
    current_player('X')
{}

bool GomokuGame::make_move(int row, int col) {
    if (board[row][col] == ' ') {
        board[row][col] = current_player;
        current_player = (current_player == 'X') ? 'O' : 'X';
        return true;
    }
    return false;
}

std::optional<char> GomokuGame::check_winner() const {
    const std::vector<std::pair<int, int>> directions = { {0, 1}, {1, 0}, {1, 1}, {1, -1} };
    for (int row = 0; row < board_size; ++row) {
        for (int col = 0; col < board_size; ++col) {
            if (board[row][col] != ' ') {
                for (const auto& direction : directions) {
                    if (_check_five_in_a_row(row, col, direction)) {
                        return board[row][col];
                    }
                }
            }
        }
    }
    return std::nullopt;
}

std::vector<std::vector<char>> GomokuGame::getBoard() const {
    return board;
}

bool GomokuGame::_check_five_in_a_row(int row, int col, std::pair<int, int> direction) const {
    int dx = direction.first;
    int dy = direction.second;
    int count = 1;
    char symbol = board[row][col];
    for (int i = 1; i < 5; ++i) {
        int new_row = row + dx * i;
        int new_col = col + dy * i;
        if (!(0 <= new_row && new_row < board_size && 0 <= new_col && new_col < board_size)) {
            return false;
        }
        if (board[new_row][new_col] != symbol) {
            return false;
        }
        ++count;
    }
    return count == 5;
}

#include <boost/iostreams/device/file.hpp>
#include <boost/iostreams/filtering_stream.hpp>
#include <boost/iostreams/filtering_streambuf.hpp>
#include <fstream>
#include <iostream>

#include <string>
#include <vector>
#include <boost/filesystem.hpp>
#include <zip.h>

struct ZipFileInfo {
    std::string filename;
    std::string mode;
};

class ZipFileProcessor {
public:
    explicit ZipFileProcessor(const std::string& zip_file_path);

    bool extract_all(const std::string& output_directory);
    bool extract_file(const std::string& file_name, const std::string& output_directory);
    bool create_zip_file(const std::vector<std::string>& files, const std::string& output_zip_file);
    ZipFileInfo read_zip_file();  

private:
    bool extract_file_from_zip(zip_t* archive, zip_int64_t index, const std::string& output_file_path);
    bool create_directory_if_not_exists(const std::string& dir_path);
    zip_t* open_zip_file(int flags);

    std::string zip_file_path_;
};


ZipFileProcessor::ZipFileProcessor(const std::string& zip_file_path)
    : zip_file_path_(zip_file_path) {}

ZipFileInfo ZipFileProcessor::read_zip_file() {
    zip_t* archive = open_zip_file(ZIP_RDONLY);
    ZipFileInfo info;
    if (archive) {
        info.filename = zip_file_path_;  
        info.mode = "r";  
        zip_close(archive);
    }
    return info;
}


bool ZipFileProcessor::extract_all(const std::string& output_directory) {

    if (output_directory.empty()) {
        return false; 
    }

    if (!create_directory_if_not_exists(output_directory)) {

        return false;
    }


    zip_t* archive = open_zip_file(ZIP_RDONLY);
    if (!archive) return false;


    bool success = true;
    zip_int64_t num_entries = zip_get_num_entries(archive, ZIP_FL_UNCHANGED);
    for (zip_int64_t i = 0; i < num_entries; ++i) {
        zip_stat_t stat;
        if (zip_stat_index(archive, i, ZIP_FL_ENC_UTF_8, &stat) == 0) {
            std::string output_file_path = output_directory + "/" + stat.name;
            if (!extract_file_from_zip(archive, i, output_file_path)) {
                success = false;
            }
        }
    }

    zip_close(archive);
    return success;
}

bool ZipFileProcessor::extract_file(const std::string& file_name, const std::string& output_directory) {

    if (output_directory.empty()) {
        return false;
    }

    if (!create_directory_if_not_exists(output_directory)) {
        std::cerr << "Failed to create output directory: " << output_directory << std::endl;
        return false;
    }


    zip_t* archive = open_zip_file(ZIP_RDONLY);
    if (!archive) return false;


    zip_int64_t index = zip_name_locate(archive, file_name.c_str(), ZIP_FL_ENC_UTF_8);
    if (index < 0) {
        std::cerr << "File not found in zip: " << file_name << std::endl;
        zip_close(archive);
        return false;
    }

    std::string output_file_path = output_directory + "/" + file_name;
    bool success = extract_file_from_zip(archive, index, output_file_path);

    zip_close(archive);
    return success;
}

bool ZipFileProcessor::create_zip_file(const std::vector<std::string>& files, const std::string& output_zip_file) {
    zip_t* archive = zip_open(output_zip_file.c_str(), ZIP_CREATE | ZIP_TRUNCATE, nullptr);
    if (!archive) {
        std::cerr << "Error opening zip file: " << output_zip_file << std::endl;
        return false;
    }

    for (const auto& file_path : files) {
        zip_source_t* source = zip_source_file(archive, file_path.c_str(), 0, 0);
        if (!source) {
            std::cerr << "Error creating zip source for file: " << file_path << std::endl;
            zip_close(archive);
            return false;
        }

        if (zip_file_add(archive, file_path.c_str(), source, ZIP_FL_ENC_UTF_8) < 0) {
            std::cerr << "Error adding file to zip: " << file_path << std::endl;
            zip_source_free(source);
            zip_close(archive);
            return false;
        }
    }

    if (zip_close(archive) < 0) {
        std::cerr << "Error closing zip file: " << output_zip_file << std::endl;
        return false;
    }

    return true;
}


bool ZipFileProcessor::extract_file_from_zip(zip_t* archive, zip_int64_t index, const std::string& output_file_path) {
    zip_file_t* file = zip_fopen(archive, zip_get_name(archive, index, ZIP_FL_ENC_UTF_8), ZIP_FL_UNCHANGED);
    if (!file) {
        std::cerr << "Failed to open file in zip: " << zip_get_name(archive, index, ZIP_FL_ENC_UTF_8) << std::endl;
        return false;
    }

    std::ofstream out_file(output_file_path, std::ios::binary);
    if (!out_file.is_open()) {
        std::cerr << "Failed to open output file: " << output_file_path << std::endl;
        zip_fclose(file);
        return false;
    }

    char buffer[4096];
    zip_int64_t bytes_read;
    while ((bytes_read = zip_fread(file, buffer, sizeof(buffer))) > 0) {
        out_file.write(buffer, bytes_read);
        if (!out_file) {
            std::cerr << "Failed to write to file: " << output_file_path << std::endl;
            zip_fclose(file);
            return false;
        }
    }

    zip_fclose(file);
    return true;
}

bool ZipFileProcessor::create_directory_if_not_exists(const std::string& dir_path) {
    namespace fs = boost::filesystem;
    if (!fs::exists(dir_path)) {
        return fs::create_directories(dir_path);
    }
    return true;
}



zip_t* ZipFileProcessor::open_zip_file(int flags) {
    zip_t* archive = zip_open(zip_file_path_.c_str(), flags, nullptr);
    if (!archive) {
        std::cerr << "Failed to open zip file: " << zip_file_path_ << std::endl;
    }
    return archive;
}

