class SQLGenerator {
public:
    SQLGenerator(const std::string& table_name);

    std::string select(const std::vector<std::string>& fields = {}, const std::string& condition = "") const;
    std::string insert(const std::map<std::string, std::string>& data) const;
    std::string update(const std::map<std::string, std::string>& data, const std::string& condition) const;
    std::string delete_query(const std::string& condition) const;
    std::string select_female_under_age(int age) const;
    std::string select_by_age_range(int min_age, int max_age) const;

private:
    std::string table_name;
};


SQLGenerator::SQLGenerator(const std::string& table_name) : table_name(table_name) {}


std::string SQLGenerator::select(const std::vector<std::string>& fields, const std::string& condition) const {
    std::string fields_str = "*";
    if (!fields.empty()) {
        fields_str = fields[0];
        for (size_t i = 1; i < fields.size(); ++i) {
            fields_str += ", " + fields[i];
        }
    }

    std::string sql = "SELECT " + fields_str + " FROM " + table_name;
    if (!condition.empty()) {
        sql += " WHERE " + condition;
    }
    return sql + ";";
}


std::string SQLGenerator::insert(const std::map<std::string, std::string>& data) const {
    std::ostringstream fields_stream, values_stream;

    for (auto it = data.begin(); it != data.end(); ++it) {
        if (it != data.begin()) {
            fields_stream << ", ";
            values_stream << ", ";
        }
        fields_stream << it->first;
        values_stream << "'" << it->second << "'";
    }

    std::string sql = "INSERT INTO " + table_name + " (" + fields_stream.str() + ") VALUES (" + values_stream.str() + ")";
    return sql + ";";
}


std::string SQLGenerator::update(const std::map<std::string, std::string>& data, const std::string& condition) const {
    std::ostringstream set_clause_stream;

    for (auto it = data.begin(); it != data.end(); ++it) {
        if (it != data.begin()) {
            set_clause_stream << ", ";
        }
        set_clause_stream << it->first << " = '" << it->second << "'";
    }

    std::string sql = "UPDATE " + table_name + " SET " + set_clause_stream.str();
    if (!condition.empty()) {
        sql += " WHERE " + condition;
    }
    return sql + ";";
}


std::string SQLGenerator::delete_query(const std::string& condition) const {
    std::string sql = "DELETE FROM " + table_name;
    if (!condition.empty()) {
        sql += " WHERE " + condition;
    }
    return sql + ";";
}


std::string SQLGenerator::select_female_under_age(int age) const {
    std::string condition = "age < " + std::to_string(age) + " AND gender = 'female'";
    return select({}, condition);
}


std::string SQLGenerator::select_by_age_range(int min_age, int max_age) const {
    std::string condition = "age BETWEEN " + std::to_string(min_age) + " AND " + std::to_string(max_age);
    return select({}, condition);
}

#include <vector>

class ChandrasekharSieve {
public:
    ChandrasekharSieve(int n);
    std::vector<int> get_primes() const;

private:
    int n;
    std::vector<int> primes;

    std::vector<int> generate_primes();
};

ChandrasekharSieve::ChandrasekharSieve(int n) : n(n) {
    primes = generate_primes();
}

std::vector<int> ChandrasekharSieve::generate_primes() {
    if (n < 2) {
        return {};
    }

    std::vector<bool> sieve(n + 1, true);
    sieve[0] = sieve[1] = false;

    int p = 2;
    while (p * p <= n) {
        if (sieve[p]) {
            for (int i = p * p; i <= n; i += p) {
                sieve[i] = false;
            }
        }
        p++;
    }

    std::vector<int> primes;
    for (int i = 2; i <= n; i++) {
        if (sieve[i]) {
            primes.push_back(i);
        }
    }

    return primes;
}

std::vector<int> ChandrasekharSieve::get_primes() const {
    return primes;
}

#include <vector>
#include <string>

class ArrangementCalculator {
public:
    ArrangementCalculator(const std::vector<std::string>& datas);
    static int count(int n, int m = -1);
    static int count_all(int n);
    std::vector<std::vector<std::string>> select(int m = -1) const;
    std::vector<std::vector<std::string>> select_all() const;
    static int factorial(int n);

private:
    std::vector<std::string> datas;
    void permutations_recursive(std::vector<std::string>& current, std::vector<std::string>& remaining, int m, std::vector<std::vector<std::string>>& result) const;
};

ArrangementCalculator::ArrangementCalculator(const std::vector<std::string>& datas) : datas(datas) {}

int ArrangementCalculator::count(int n, int m) {
    if (m == -1 || n == m) {
        return ArrangementCalculator::factorial(n);
    }
    else {
        return ArrangementCalculator::factorial(n) / ArrangementCalculator::factorial(n - m);
    }
}

int ArrangementCalculator::count_all(int n) {
    int total = 0;
    for (int i = 1; i <= n; ++i) {
        total += ArrangementCalculator::count(n, i);
    }
    return total;
}

void ArrangementCalculator::permutations_recursive(std::vector<std::string>& current, std::vector<std::string>& remaining, int m, std::vector<std::vector<std::string>>& result) const {
    if (current.size() == m) {
        result.push_back(current);
    }
    else {
        for (size_t i = 0; i < remaining.size(); ++i) {
            std::vector<std::string> new_current = current;
            new_current.push_back(remaining[i]);
            std::vector<std::string> new_remaining = remaining;
            new_remaining.erase(new_remaining.begin() + i);
            permutations_recursive(new_current, new_remaining, m, result);
        }
    }
}

std::vector<std::vector<std::string>> ArrangementCalculator::select(int m) const {
    std::vector<std::vector<std::string>> result;
    if (m == -1) {
        m = datas.size();
    }

    std::vector<std::string> current;
    std::vector<std::string> remaining = datas;

    permutations_recursive(current, remaining, m, result);

    return result;
}

std::vector<std::vector<std::string>> ArrangementCalculator::select_all() const {
    std::vector<std::vector<std::string>> result;
    for (size_t i = 1; i <= datas.size(); ++i) {
        std::vector<std::vector<std::string>> partial_result = select(i);
        result.insert(result.end(), partial_result.begin(), partial_result.end());
    }
    return result;
}

int ArrangementCalculator::factorial(int n) {
    int result = 1;
    for (int i = 2; i <= n; ++i) {
        result *= i;
    }
    return result;
}

